breakpoints.scss:
@use 'functions' as *;

$breakpoints: (
	phone: pxToEm(360),
	tablet: pxToEm(680),
	desktop: pxToEm(1120),
);

functions.scss:
@use 'sass:math';

// calculate REM
@function calculateRem($size) {
    $remSize: math.div($size, 16px);
    @return $remSize * 1rem;
}

// convert px to em
@function pxToEm($pixel) {
    @if math.is-unitless($pixel) {
        @return math.div($pixel, 16) + em;
    } @else {
        @error "Don't use units when using the pxToEm() function.";
    }
}

mixins.scss:
@use 'functions' as *;
@use 'breakpoints' as *;

// font-size
@mixin font-size($size) {
	font-size: calculateRem($size);
}

// clamp font-size medium
@mixin clamp-fs-medium($min-size, $max-size) {
	font-size: clamp(calculateRem($min-size), 2.5vw + 0.5rem, calculateRem($max-size));
}

// clamp font-size small
@mixin clamp-fs-small($min-size, $max-size) {
	font-size: clamp(calculateRem($min-size), 1vw, calculateRem($max-size));
}

// media queries
@mixin device($key) {
	$size: map-get($breakpoints, $key);

	@media only screen and (min-width: $size) {
		@content;
	}
}

// hover
@mixin hover {
	@media screen and (hover: hover) {
		@content;
	}
}

// touch -> active
@mixin touch {
	@media screen and (hover: none) {
		@content;
	}
}

reset.scss:
/*
1. position
2. box model
3. typography
4. visual
5. others
*/

/* Box sizing rules */
*,
*::before,
*::after {
	box-sizing: border-box;
}

/* Remove default margin, padding and font */
/* prevent grid/flex items from spilling out of their container */
* {
	margin: 0;
	padding: 0;
	min-width: 0;
	font: inherit;
}

/* dark mode user-agent-styles */
/* improves punctuation in supported browsers */
html {
	font-size: 100%;
	hanging-punctuation: first last;
	overflow-x: hidden;
	color-scheme: dark light;
}

/* Set core root defaults */
html:focus-within {
	scroll-behavior: smooth;
}

/* Remove all animations, transitions and smooth scroll for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
	html:focus-within {
		scroll-behavior: auto;
	}

	*,
	*::before,
	*::after {
		animation-duration: 0.01ms !important;
		animation-iteration-count: 1 !important;
		transition-duration: 0.01ms !important;
		scroll-behavior: auto !important;
	}
}

/* Add smooth scroll on targets*/
/*for sticky header or just add space*/
@media (prefers-reduced-motion: no-preference) {
	:has(:target) {
		scroll-padding-top: 3rem;
		scroll-behavior: smooth;
	}
}

/* Set core body defaults */
body {
	/* Ensure that the body fills the entire viewport. */
	min-height: 100dvh;
	line-height: 1.5;
	text-rendering: optimizeSpeed;
}

/* Remove list styles on ul, ol elements with a list role, which suggests default styling will be removed */
ul[role='list'],
ol[role='list'] {
	list-style: none;
}

/* Remove decoration to anchor elements  */
a,
a:visited,
a:active {
	text-decoration: none;
}

/* Anchor elements that don't have a class get default styles */
a:not([class]) {
	text-decoration-skip-ink: auto;
}

/* Responsive images/videos */
img,
picture,
svg,
video {
	display: block;
	max-width: 100%;
}

img {
	height: auto;
	vertical-align: middle;
	font-style: italic;
	background-repeat: no-repeat;
	background-size: cover;
	shape-margin: 0.75rem;
}

/* Inherit fonts for inputs and buttons */
input,
button,
textarea,
select {
	font: inherit;
}

/* spin button off for webkit (chrome, safari) */
input[type='number']::-webkit-inner-spin-button,
input[type='number']::-webkit-outer-spin-button {
	-webkit-appearance: none;
	margin: 0;
}

input[type='number'] {
	-moz-appearance: textfield; /* spin button off for firefox */
	appearance: textfield; /* standard */
}

/* Balance headings across multiple lines into an even block */
h1,
h2,
h3,
h4,
h5,
h6 {
	text-wrap: balance;
}

/* improve readability with max-width on paragraphs and lists */
/* prevent orphans in supported browsers */
p,
li {
	max-width: 65ch;
	text-wrap: pretty;
}
